	public TeamSprite(ImageConfig imageConfig)
	{
		super(imageConfig);
	}
	@Override
	public void update()
	{
		if(state==START)
		{
			if(this.nextScriptInt(mVerts.length))
			{
				this.setState(STAY);
			}
		}
		
	}
	@Override
	public void drawView(Canvas canvas)
	{
		if(state==START)
		{
			this.drawImage(canvas, ImageConfig.MENU_PAUSE01, 
			               x, y, mTexs, mVerts[scriptInt], mIndices);
		}
		else if(state==STAY)
		{
			this.drawImage(canvas, ImageConfig.MENU_PAUSE01, 
			               x, y, mTexs, mVerts[mVerts.length-1], mIndices);
			for(int i=0;i<GameConsts.TEAM_TEXT.length;i++)
			{
				this.drawText(canvas, GameConsts.TEAM_TEXT[i], 
				              x+GameConsts.TEAM_WORD_POSITION[i][0], 
				              y+GameConsts.TEAM_WORD_POSITION[i][1], 
				              GameConsts.TEAM_WORD_POSITION[i][2], 
				              GameConsts.TEAM_WORD_POSITION[i][3], 
				              GameConsts.TEAM_WORD_POSITION[i][4], 
				              GameConsts.TEAM_WORD_POSITION[i][5], 
				              GameConsts.TEAM_WORD_POSITION[i][6], 
				              GameConsts.TEAM_WORD_POSITION[i][7], 
				              GameConsts.TEAM_WORD_POSITION[i][8]);
			}
		}
	}
	
