	private int hp = 0;
	private int maxHp = 100;
	private boolean pause;
	private long timeInt;
	private int time;
	private int maxTime;
	private int stage;
	private int bombInt=3;

	public UiSprite(ImageConfig imageConfig)
	{
		super(imageConfig);
	}
	@Override
	public void update()
	{
		if(state==REALLY)
		{
			if(this.nextScriptInt(REALLY_SCRIPT.length))
			{
				this.setState(REALLY_END);
			}
		}
		else if (state == BAR_START)
		{
			if (this.nextScriptInt(GameConsts.BAR_START_SCRIPT.length))
			{
				this.setState(BAR_STAY);
			}
		}
		else if (state == BAR_STAY)
		{
			
			if (time < maxTime)
			{
				timeInt++;
				if (timeInt%5==0)
				{
					time++;
				}
			}
		}
		else if (state == WIN)
		{
			if(this.nextScriptInt(winVerts.length))
			{
				this.setState(WIN_END);
			}
		}
		else if(state == WIN_END)
		{
			this.nextScriptInt(winEndVerts.length);
		}
		else if(state == LOSE)
		{
			if(this.nextScriptInt(winVerts.length))
			{
				this.setState(LOSE_END);
			}
		}
		else if(state == LOSE_END)
		{
			this.nextScriptInt(winEndVerts.length);
		}
	}
	@Override
	public void drawView(Canvas canvas)
	{
		if (state == BAR_START)
		{
			int width = this.getWidth(ImageConfig.UI_BAR)/3;
			int height =this.getHeight(ImageConfig.UI_BAR);
			this.drawFrames(canvas, ImageConfig.UI_BAR, x, y, width, height, GameConsts.BAR_START_SCRIPT[scriptInt]);
		}
		else if (state == BAR_STAY)
		{
			this.drawBar(canvas);
		}
		else if (state == MENU_PAUSE)
		{
			this.drawImage(canvas, ImageConfig.MENU_PAUSE01, x, y);
			this.drawImage(canvas, ImageConfig.MENU_PAUSE02, 
			               x+GameConsts.MENU_PAUSE_POSITION[2], 
			               y+GameConsts.MENU_PAUSE_POSITION[3]);
			this.drawText(canvas, GameConsts.MENU_TEXT, 
			              x+GameConsts.MENU_POSITION[0], 
			              y+GameConsts.MENU_POSITION[1], 
			              GameConsts.MENU_POSITION[2], 
			              GameConsts.MENU_POSITION[3], 
			              GameConsts.MENU_POSITION[4], 
			              GameConsts.MENU_POSITION[5], 
			              GameConsts.MENU_POSITION[6],
			              GameConsts.MENU_POSITION[7],
			              GameConsts.MENU_POSITION[8]);	
		}
		else if( state == REALLY )
		{
			if(REALLY_SCRIPT[scriptInt]==1||REALLY_SCRIPT[scriptInt]==2)
			{
				this.drawImage(canvas, ImageConfig.UI_GO, 
				               x+GameConsts.UI_REALLY_POSITION[0], 
				               y+GameConsts.UI_REALLY_POSITION[1]);
			}
			this.drawImage(canvas, ImageConfig.UI_WOOD, x, y);
			if(REALLY_SCRIPT[scriptInt]==0||REALLY_SCRIPT[scriptInt]==1)
			{
				this.drawImage(canvas, ImageConfig.UI_REALLY, 
				               x+GameConsts.UI_REALLY_POSITION[0], 
				               y+GameConsts.UI_REALLY_POSITION[1], mTexs, mVerts[scriptInt], mIndices);
			}
						
		}
		else if( state==WIN)
		{
			this.drawImage(canvas, ImageConfig.UI_WOOD, x, y);
			this.drawImage(canvas, ImageConfig.WIN, 
			               x+GameConsts.UI_REALLY_POSITION[0], 
			               y+GameConsts.UI_REALLY_POSITION[1], winTexs, winVerts[scriptInt], winIndices);
		}
		else if(state==WIN_END)
		{
			this.drawImage(canvas, ImageConfig.UI_WOOD, x, y);
			this.drawImage(canvas, ImageConfig.WIN, 
			               x+GameConsts.UI_REALLY_POSITION[0], 
			               y+GameConsts.UI_REALLY_POSITION[1], winTexs, winEndVerts[scriptInt], winIndices);

		}
		else if(state==LOSE)
		{
			this.drawImage(canvas, ImageConfig.UI_WOOD, x, y);
			this.drawImage(canvas, ImageConfig.LOSE, 
			               x+GameConsts.UI_REALLY_POSITION[0], 
			               y+GameConsts.UI_REALLY_POSITION[1], winTexs, winVerts[scriptInt], winIndices);
		}
		else if(state==LOSE_END)
		{
			this.drawImage(canvas, ImageConfig.UI_WOOD, x, y);
			this.drawImage(canvas, ImageConfig.LOSE, 
			               x+GameConsts.UI_REALLY_POSITION[0], 
			               y+GameConsts.UI_REALLY_POSITION[1], winTexs, winEndVerts[scriptInt], winIndices);

		}
	}

	private void drawBar(Canvas canvas)
	{
		int index=GameConsts.BAR_START_SCRIPT.length-1;
		int width = this.getWidth(ImageConfig.UI_BAR)/3;
		int height =this.getHeight(ImageConfig.UI_BAR);
		this.drawFrames(canvas, ImageConfig.UI_BAR, x, y, width, height, GameConsts.BAR_START_SCRIPT[index]);
		this.drawRect(canvas, x + GameConsts.BAR_TIME_POSITION[0], y
				+ GameConsts.BAR_TIME_POSITION[1], GameConsts.BAR_TIME_POSITION[2]
				* (maxTime - time) / maxTime, GameConsts.BAR_TIME_POSITION[3], 255, 0, 0, 255);
		this.drawStrokeRect(canvas,
							x + GameConsts.BAR_TIME_POSITION[0],
							y + GameConsts.BAR_TIME_POSITION[1],
							GameConsts.BAR_TIME_POSITION[2],
							GameConsts.BAR_TIME_POSITION[3],
							255,
							0,
							0,
							0,
							GameConsts.BAR_TIME_POSITION[4]);
		this.drawRect(	canvas,
						x + GameConsts.BAR_HP_POSITION[0],
						y + GameConsts.BAR_HP_POSITION[1],
						GameConsts.BAR_HP_POSITION[2] * hp / maxHp,
						GameConsts.BAR_HP_POSITION[3],
						255,
						255,
						0,
						0);
		this.drawStrokeRect(canvas,
							x + GameConsts.BAR_HP_POSITION[0],
							y + GameConsts.BAR_HP_POSITION[1],
							GameConsts.BAR_HP_POSITION[2],
							GameConsts.BAR_HP_POSITION[3],
							255,
							0,
							0,
							0,
							GameConsts.BAR_HP_POSITION[4]);
		this.drawText(canvas, GameConsts.BAR_TEXT+stage, 
		              x+GameConsts.BAR_WORD_POSITION[0], 
		              y+GameConsts.BAR_WORD_POSITION[1], 
		              GameConsts.BAR_WORD_POSITION[2],
		              GameConsts.BAR_WORD_POSITION[3],
		              GameConsts.BAR_WORD_POSITION[4], 
		              GameConsts.BAR_WORD_POSITION[5], 
		              GameConsts.BAR_WORD_POSITION[6], 
		              GameConsts.BAR_WORD_POSITION[7], 
		              GameConsts.BAR_WORD_POSITION[8]);
		for(int i=0;i<GameConsts.BAR_UI_POSITION.length;i++)
		{
			this.drawImage(canvas, GameConsts.BAR_UI_POSITION[i][0], 
			               x+GameConsts.BAR_UI_POSITION[i][1], 
			               y+GameConsts.BAR_UI_POSITION[i][2]);
		}
		if (pause)
		{
			this.drawImage(	canvas,
							ImageConfig.BUTTON_CONTINUE,
							x + GameConsts.BUTTON_POSITION[0],
							y + GameConsts.BUTTON_POSITION[1]);
		}
		else
		{
			this.drawImage(canvas, ImageConfig.BUTTON_PAUSE, x + GameConsts.BUTTON_POSITION[0], y
					+ GameConsts.BUTTON_POSITION[1]);

		}
		this.drawBomb(canvas);
	}
	
	private void drawBomb(Canvas canvas)
	{
		for(int i=0;i<bombInt;i++)
		{
			this.drawImage(canvas, ImageConfig.UI_BOMB, 
			               x+GameConsts.BOMB_POSITION[0]+GameConsts.BOMB_POSITION[2]*i, 
			               y+GameConsts.BOMB_POSITION[1]);
		}
	}
	public boolean isCollisionMenu(int type,int x,int y)
	{
		return this.isCollision(x, y,GameConsts.MENU_COLLISION[type]);
	}
